from webbrowser import open as web_open
import hashlib
import os
import zipfile
from time import sleep
import sys
import base64
if os.path.basename(sys.executable) != 'python.exe':
    os.chdir(os.path.dirname(sys.executable))
def encrypt(fpath: str, algorithm: str) -> str:
    with open(fpath, 'rb') as f:
        return hashlib.new(algorithm, f.read()).hexdigest()
if not os.path.isfile("Tools.zip"):
    print("请打开浏览器下载搭建文件并将文件复制到程序目录下")
    print("按下回车打开下载页面...")
    input()
    web_open("https://wwo.lanzoum.com/ixA9K1wvft2b")
    print("等待下载完成(将文件复制到本目录后自动继续)...")
    while not os.path.isfile('Tools.zip'):
        pass
sleep(1)
print("正在验证文件完整性...")
# print(encrypt('Tools.zip', 'md5'))
if encrypt('Tools.zip', 'md5') == '1dc8bc2b6fef8a0933f15c419f9ef99e':
    pass
elif encrypt('Tools.zip', 'md5') == '65e83fcb0f3a0f6729d24a24794eefb5':
    print("失败,检测到您正在使用旧版搭建包,请删除现有搭建包重新打开工具下载新版搭建包!")
    input()
    sys.exit(0)
else:
    print("失败,请删除文件打开工具下载搭建文件!")
    input()
    sys.exit(0)
